mapper.select:
SELECT A.*, M.name AS extra__writer FROM article AS A INNER JOIN member AS M ON A.memberId = M.id WHERE 1
AND A.boardId = #{boardId}
AND A.title LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY A.id DESC
LIMIT #{limitFrom}, #{limitTake}

SELECT COUNT(*) FROM article WHERE 1
AND boardId = #{boardId}
AND title LIKE CONCAT('%', #{searchKeyword}, '%')
